// Node.js built-in crypto module examples
const crypto = require('crypto');

// Example 1: Hash a string with SHA256
function hashString(text, algorithm = 'sha256') {
  return crypto
    .createHash(algorithm)
    .update(text)
    .digest('hex');
}

// Example 2: HMAC signature for API authentication
function createHMAC(message, secret, algorithm = 'sha256') {
  return crypto
    .createHmac(algorithm, secret)
    .update(message)
    .digest('hex');
}

// Example 3: Generate random token
function generateToken(length = 32) {
  return crypto
    .randomBytes(Math.ceil(length / 2))
    .toString('hex')
    .slice(0, length);
}

// Example 4: Encrypt with AES-256-GCM
function encrypt(text, password) {
  const algorithm = 'aes-256-gcm';
  const key = crypto.scryptSync(password, 'salt', 32);
  const iv = crypto.randomBytes(16);
  const cipher = crypto.createCipheriv(algorithm, key, iv);

  let encrypted = cipher.update(text, 'utf8', 'hex');
  encrypted += cipher.final('hex');

  const authTag = cipher.getAuthTag();

  return {
    encrypted: encrypted,
    iv: iv.toString('hex'),
    authTag: authTag.toString('hex')
  };
}

// Example 5: Create UUID
function generateUUID() {
  return crypto.randomUUID();
}

// Run examples
const text = 'Hello, n8n with JavaScript!';
const password = 'my-secret-key';

const results = {
  hashes: {
    md5: hashString(text, 'md5'),
    sha1: hashString(text, 'sha1'),
    sha256: hashString(text, 'sha256'),
    sha512: hashString(text, 'sha512')
  },
  hmac: createHMAC(text, 'secret-key'),
  random: {
    token32: generateToken(32),
    token16: generateToken(16),
    uuid: generateUUID()
  },
  encrypted: encrypt(text, password)
};

return results;
