const { execSync } = require('child_process');

try {
  const version = execSync('ffmpeg -version', { encoding: 'utf-8' });
  const versionLines = version.split('\n').slice(0, 3);

  return {
    ffmpegStatus: 'Working âœ“',
    version: versionLines.join('\n'),
    exampleCommand: 'ffmpeg -i input.mp4 -vf scale=320:240 output.mp4',
    docLink: 'https://ffmpeg.org/documentation.html'
  };
} catch (error) {
  return {
    error: 'FFmpeg not found',
    message: error.message
  };
}
