const moment = require('moment');

const now = moment();

return {
  current: {
    full: now.format('YYYY-MM-DD HH:mm:ss'),
    iso: now.toISOString(),
    unix: now.unix(),
    readable: now.format('dddd, MMMM Do, YYYY'),
    relative: now.fromNow()
  },
  calculations: {
    nextWeek: now.add(7, 'days').format('YYYY-MM-DD'),
    lastMonth: now.subtract(1, 'month').format('YYYY-MM-DD'),
    startOfYear: now.startOf('year').format('YYYY-MM-DD'),
    endOfMonth: now.endOf('month').format('YYYY-MM-DD')
  },
  formats: {
    timeOnly: now.format('LT'),
    dateOnly: now.format('L'),
    calendar: now.calendar()
  },
  momentStatus: 'Working âœ“'
};
